def task8(input):
    if (len(input)) < 13:
        raise ValueException("List must have 13 elements minimum")
    max_product = 0
    for i in range(0,len(input)-12):
        cur_product = input[i]
        for j in range(1,13):
            cur_product *= input[i+j]
        if cur_product > max_product:
            max_product = cur_product
    return max_product

def test_task8():
    input = ""
    assert 23514624000 == task8(list(map(int, list(input))))

def task23(input):
    limit = input + 1
    divisors_sum = [0] * limit
    for i in range(1, limit):
        for j in range(i * 2, limit, i):
            divisors_sum[j] += i
    ab_nums = [i for (i, x) in enumerate(divisors_sum) if x > i]

    sums = [False] * limit
    for i in ab_nums:
        for j in ab_nums:
            if i + j < limit:
                sums[i + j] = True
            else:
                break
    ans = sum(i for (i, x) in enumerate(sums) if not x)
    return ans

def test_task23():
    input = 28123
    assert task23(input) == 4179871

if __name__ == "__main__":
    input = 28123
    result = task23(input)
    print(result)